import React, { Component } from 'react';
import { Redirect, Route, RouteProps } from 'react-router-dom';

import { ReactKeycloakInjectedProps, useKeycloak, withKeycloak } from '@react-keycloak/web';
import { App } from "../components";

export const ProtectedRoute: React.FC<RouteProps> = (props: RouteProps) => {
    const [keycloak] = useKeycloak();

    if (keycloak.authenticated) {
        const renderComponent = (props: RouteProps) => <Component { ...props }/>;
        return <Route { ...props } render={ renderComponent }/>;
    } else {
        const renderComponent = (props: RouteProps) => <Redirect
            to={ {pathname: '/login', state: {from: props.location}} }/>;
        return <Route { ...props } render={ renderComponent }/>;
    }
}

export const LoginPage: React.FC = withKeycloak(({ keycloak, keycloakInitialized }) => {
    // Here you can access all of keycloak methods and variables.
    // See https://www.keycloak.org/docs/latest/securing_apps/index.html#javascript-adapter-reference
    return
        <App/>

})
