import React, { Component } from 'react';
import { Redirect, Route, RouteProps } from 'react-router-dom';

import { useKeycloak } from '@react-keycloak/web';

export const ProtectedRoute: React.FC<RouteProps> = (props: RouteProps) => {
    const [keycloak, initialized] = useKeycloak();

    if (keycloak.authenticated) {
        const renderComponent = (props: RouteProps) => <Component { ...props }/>;
        return <Route { ...props } render={ renderComponent }/>;
    } else {
        const renderComponent = (props: RouteProps) => <Redirect
            to={ {pathname: '/login', state: {from: props.location}} }/>;
        return <Route { ...props } render={ renderComponent }/>;
    }
}

export const PrivateRoute: React.FC<RouteProps> = (props: RouteProps) => {
    const [keycloak, initialized] = useKeycloak();

    if (keycloak.authenticated) {
        const renderComponent = (props: RouteProps) => <Component { ...props }/>;
        return <Route { ...props } render={ renderComponent }/>;
    } else {
        keycloak.login();
        return null;
            // return <Redirect to={keycloak.createLoginUrl()}/>;
        // const renderComponent = (props: RouteProps) => <Redirect
        //     to={ {pathname: '/login', state: {from: props.location}} }/>;
        // return <Route { ...props } render={ renderComponent }/>;
    }
}
